section Section1;
shared Empdata = let AdfDoc = AzureStorage.BlobContents("https://storageadfdemo1.blob.core.windows.net/container-adfdemo/input/Emp_Data.csv"),Csv = Csv.Document(AdfDoc, [Delimiter = ",", Encoding = TextEncoding.Utf8, QuoteStyle = QuoteStyle.Csv]), PromotedHeaders = Table.PromoteHeaders(Csv, [PromoteAllScalars = true]) in  PromotedHeaders;
