shared UserQuery = let Source = #"Empdata",
  #"Grouped rows" = Table.Group(Source, {"DeptNo"}, {{"No_Of_Emp", each Table.RowCount(_), Int64.Type}}),
  #"Merged queries" = Table.NestedJoin(#"Grouped rows", {"DeptNo"}, DeptData, {"DeptNo"}, "DeptData", JoinKind.Inner),
